mapper.select:
SELECT u.id, u.user_oid, u.login, u.activated, u.status, u.language, u.password_hash password, u.deleted, u.tenant_id, u.lock_status, u.lock_date_deadline, u.user_name, u.email, u.mobile, u.remark FROM sys_user u
left JOIN sys_user_login_bind b on b.user_oid=u.user_oid
WHERE 1=1
AND u.id = #{id}
AND u.user_oid = #{userOid}
AND u.login = #{login}
AND b.enabled = true and b.is_active = true AND b.deleted = false AND b.login = #{loginBind}
AND u.mobile = #{mobile}
AND u.email = #{email}

select count(1) from sys_user_login_bind b
where b.user_oid=#{userOid}
and b.is_active=true

SELECT created_date FROM sys_password_history
WHERE user_oid=#{userOid}
order by created_date desc
