mapper.select:
SELECT cf.content_id, c.content_name, c.content_router, c.icon, c.parent_id, c.sequence_number AS contentSequenceNumber, c.has_son_content, f.id as function_id, f.function_name, f.function_router, f.param, f.sequence_number as functionSequenceNumber, f.page_id, f.function_icon, f.application_id, a.app_code as applicationCode FROM sys_function_list f LEFT JOIN sys_content_function_rel cf on cf.function_id = f.id LEFT JOIN sys_content_list c on cf.content_id = c.id and c.deleted = 0 LEFT JOIN sys_application a on f.application_id = a.id WHERE f.deleted = 0 AND exists (select 1 from sys_role_function rf where rf.function_id = f.id
AND rf.role_id in
#{roleId}
) ORDER BY c.sequence_number asc , f.sequence_number asc

select c.id as content_id, c.content_name, c.content_router, c.icon, c.parent_id, c.sequence_number AS contentsequencenumber, c.has_son_content from sys_content_list c where c.deleted = 0 and c.id not in (select cf.content_id from sys_content_function_rel cf where cf.function_id in
#{functionId}
) order by c.sequence_number asc
