mapper.delete:
delete from sys_user_role where user_id = #{userId}

    and role_id in

        #{item}

mapper.select:
SELECT sur.*, sr.role_code, sr.role_name, sda.data_authority_code, sda.data_authority_name FROM sys_user_role sur left join sys_role sr on sur.role_id = sr.id left join sys_data_authority sda on sur.data_authority_id = sda.id WHERE sr.enabled = 1 AND sr.deleted = 0 AND sur.enabled = 1 AND sur.user_id = #{userId}
and sr.role_code like concat(concat('%',#{roleCode}),'%')
and sr.role_name like concat(concat('%',#{roleName}),'%')
and sda.data_authority_name like concat(concat('%',#{dataAuthorityName}),'%')
and sur.valid_date_from >= #{validDateFrom}
and sur.valid_date_to <= #{validDateTo}
order by role_code
