mapper.select:
select ed.*,eh.requisition_number reportDocumentNumber,eh.id reportId,eh.description from exp_report_dist ed,exp_report_header eh where 1=1 and ed.source_document_id = (select ah.id from exp_application_header ah where ah.document_number = #{documentNumber}) and eh.id = ed.exp_report_header_id and eh.status in (1002,1004,2002)
and ed.exp_report_header_id in(select h.id from exp_report_header h where h.requisition_number like #{reportDocumentNumber})
