mapper.select:
select * from exp_expense_type et
where et.type_category_id = #{categoryId}
and et.type_flag = #{typeFlag, jdbcType=INTEGER}
AND et.deleted = 0
order by et.sequence asc

SELECT * FROM (
SELECT t.*
FROM exp_expense_type t
WHERE
 t.type_flag = 1

    AND t.set_of_books_id = #{setOfBooksId}


    AND t.tenant_id = #{tenantId}

) et


        AND et.enabled = #{enabled}


        AND et.code LIKE concat('%', concat(#{code,jdbcType=VARCHAR}, '%'))


        AND et.name LIKE concat('%', concat(#{name,jdbcType=VARCHAR}, '%'))


order by et.code asc

SELECT temp.*,(select name from exp_expense_type_category eetc where eetc.id = temp.type_category_id) type_category_name FROM (SELECT v.*, 0 AS user_group_flag, NULL AS user_type_id FROM (SELECT * FROM exp_expense_type t WHERE t.set_of_books_id = #{setOfBooksId} AND EXISTS ( SELECT 1 FROM exp_expense_type_company ec WHERE ec.expense_type_id = t.id AND ec.company_id = #{companyId} ) UNION ALL SELECT * FROM exp_expense_type t WHERE t.set_of_books_id = #{setOfBooksId} AND NOT EXISTS ( SELECT 1 FROM exp_expense_type_company ec WHERE ec.expense_type_id = t.id ) ) v WHERE ( EXISTS ( SELECT 1 FROM exp_expense_type_user eu WHERE eu.apply_type = '102' AND eu.expense_type_id = v.id AND eu.user_type_id = #{departmentId} ) OR NOT EXISTS ( SELECT 1 FROM exp_expense_type_user eu WHERE eu.expense_type_id = v.id ) ) UNION ALL SELECT v.*, 1 as user_group_flag, eu.user_type_id FROM (SELECT * FROM exp_expense_type t WHERE t.set_of_books_id = #{setOfBooksId} AND EXISTS ( SELECT 1 FROM exp_expense_type_company ec WHERE ec.expense_type_id = t.id AND ec.company_id = #{companyId} ) UNION ALL SELECT * FROM exp_expense_type t WHERE t.set_of_books_id = #{setOfBooksId} AND NOT EXISTS ( SELECT 1 FROM exp_expense_type_company ec WHERE ec.expense_type_id = t.id ) ) v, exp_expense_type_user eu WHERE v.id = eu.expense_type_id AND eu.apply_type = '103' ) temp WHERE temp.enabled = 1 AND temp.deleted = 0 AND temp.type_flag = #{typeFlag}
AND temp.type_category_id = #{categoryId}
AND temp.name like concat(concat('%', #{expenseTypeName}),'%')
AND temp.id in
#{id}
