mapper.select:
select t.id as departmentId, t.department_code, t.name as departmentName, (select d.name from sys_department d where d.id = t.parent_id) as parentDepartmentName, CASE t.status WHEN 101 then 1 else 0 end enabled from sys_department t where not exists ( select 1 from sys_company_associate_unit v where v.department_id = t.id and v.company_id = #{companyId} ) AND t.tenant_id = #{tenantId} and t.deleted = 0
AND ( t.department_code like concat(concat('%', #{codeName}), '%') or t.name like concat(concat('%', #{codeName}), '%') )
AND t.department_code >= #{codeFrom}
AND t.department_code <= #{codeTo}
order by t.department_code

SELECT t.id, t.department_code, t.department_oid as departmentOid, t.name as departmentName, t.path as departmentPath, t.parent_id as parentId, t.status, t.tenant_id FROM sys_department t WHERE t.tenant_id = #{tenantId} AND t.deleted = 0
AND t.department_code like concat(concat('%', #{departmentCode}) ,'%')
AND t.department_code >= #{departmentCodeFrom}
AND t.department_code <= #{departmentCodeTo}
AND t.name like concat(concat('%', #{departmentName}) ,'%')
AND #{dataAuthLabel}
AND ( t.name like concat(concat('%', #{codeName}) ,'%') or t.department_code like concat(concat('%', #{codeName}) ,'%') )
AND t.status = #{status}
AND t.id = #{id}
AND t.id IN
#{item}
AND exists ( select 1 from sys_company_associate_unit v, sys_company sc where v.department_id = t.id and sc.id = v.company_id and sc.deleted = 0 and sc.enabled = 1
and v.company_id = #{companyId}
and sc.set_of_books_id = #{setOfBooksId}
and v.enabled = 1 )

select t.enabled, t.id, t.company_code, t.company_oid as companyOid, t.name as companyName, le.entity_name as legalEntityName, t.set_of_books_id as setOfBooksId, sob.set_of_books_name as setOfBooksName, t.legal_entity_id as legalEntityId, t.tenant_id, t.company_type_code from sys_company t , sys_legal_entity le, sys_set_of_books sob where t.set_of_books_id = sob.id and t.legal_entity_id = le.id and t.tenant_id = #{ew.tenantId} and t.deleted = 0
AND t.company_code like concat(concat('%', #{ew.companyCode}) ,'%')
AND t.company_code >= #{ew.companyCodeFrom}
AND t.company_code <= #{ew.companyCodeTo}
AND t.name like concat(concat('%', #{ew.companyName}) ,'%')
AND ( t.name like concat(concat('%', #{ew.codeName}) ,'%') or t.company_code like concat(concat('%', #{ew.codeName}) ,'%') )
AND t.enabled = #{ew.enabled}
AND t.id = #{ew.id}
AND t.set_of_books_id = #{ew.setOfBooksId}
AND exists ( select 1 from sys_company_associate_unit v where v.department_id = #{ew.departmentId} and v.company_id = t.id and v.enabled = 1 )
