select t.id, t.set_of_books_id, t.company_code, t.name, v.set_of_books_name from sys_company t, sys_set_of_books v where t.set_of_books_id = v.id and t.enabled = 1
and t.tenant_id = #{tenantId}
and t.set_of_books_id = #{setOfBooksId}
and ( t.name like concat(concat('%',#{keyWord}),'%') or t.company_code like concat(concat('%',#{keyWord}),'%') )
order by v.set_of_books_code ,t.company_code

select t.path
from sys_company t
where t.id != #{companyId}
and t.parent_company_id = #{parentCompanyId}
and t.path is not null
and t.enabled = 1

SELECT c.* FROM sys_company c WHERE c.enabled = TRUE and c.id in
#{companyId}
AND c.company_code LIKE concat('%', concat(#{companyCode,jdbcType=VARCHAR}, '%'))
AND c.name LIKE concat('%', concat(#{companyName,jdbcType=VARCHAR}, '%'))
AND (c.name LIKE concat('%', concat(#{keyWord,jdbcType=VARCHAR}, '%')) or c.company_code LIKE concat('%', concat(#{keyWord,jdbcType=VARCHAR}, '%')))
AND c.company_code >= #{companyCodeFrom}
AND c.company_code <= #{companyCodeTo}
ORDER BY c.company_code

