select c.id as id, c.company_code as code, c.name as name from sys_company c where 1=1
and c.tenant_id = #{tenantId}
and c.set_of_books_id = #{setOfBooksId}
and c.id = #{companyId}
and c.company_code LIKE concat(concat('%',#{code}),'%')
and c.name LIKE concat(concat('%',#{name}),'%')

SELECT c.id id, c.company_code code, c.name name FROM sys_company c WHERE c.enabled = 1
AND c.set_of_books_id = #{setOfBooksId}
AND c.tenant_id = #{tenantId}
AND c.id = #{companyId}
AND c.company_code LIKE concat('%', concat(#{companyCode,jdbcType=VARCHAR}, '%'))
AND c.name LIKE concat('%', concat(#{companyName,jdbcType=VARCHAR}, '%'))
ORDER BY c.company_code

SELECT * FROM sys_company c WHERE c.ENABLED = 1 AND c.set_of_books_id = #{setOfBooksId}
AND ( c.COMPANY_CODE LIKE concat(concat('%',#{keyword}),'%') OR c.NAME LIKE concat(concat('%',#{keyword}),'%') )
AND c.COMPANY_CODE >= #{codeFrom}
AND c.COMPANY_CODE <= #{codeTo}
ORDER BY c.COMPANY_CODE
