SELECT c.id, c.user_oid, c.user_id, c.full_name, c.employee_id, c.title, c.duty, c.rank_info, c.email, c.gender, c.entry_date, c.birthday, c.corporation_oid, c.direct_manager, c.company_id, c.tenant_id, c.status, c.leaving_date, p.PHONE_NUMBER mobile
, cc.company_oid, cc.set_of_books_id, cc.company_code, cc.name company_name
,l.entity_name legal_entity_name
, d.name department_name, d.path department_path, d.department_oid, d.id department_id
,aso.set_of_books_name
FROM sys_contact c LEFT JOIN sys_phone p ON (c.id = p.contact_id AND p.is_primary = 1)
LEFT JOIN sys_company cc ON cc.id = c.company_id
LEFT JOIN sys_legal_entity l ON (l.legal_entity_oid = c.corporation_oid and l.tenant_id = c.tenant_id)
LEFT JOIN sys_department_user du ON du.user_id = c.user_id LEFT JOIN sys_department d ON d.id = du.department_id
inner join sys_set_of_books aso on aso.id = cc.set_of_books_id
LEFT JOIN sys_user_group_user ugu ON ugu.user_id = c.user_id LEFT JOIN sys_user_group ug on ug.id=ugu.user_group_id
WHERE 1 = 1
AND c.id = #{id}
AND c.user_id = #{userId}
AND c.user_oid = #{userOid}
AND c.user_oid != #{currentUserOid}
and c.status in (1001,1002)
AND c.id != #{currentContactId}
AND c.user_oid
not
IN
#{item}
AND c.user_id
not
IN
#{item}
AND c.id
not
IN
#{item}
AND c.status = #{status}
and c.tenant_id = #{tenantId}
and c.company_id = #{companyId}
AND cc.company_oid IN
#{companyOid}
and d.department_oid = #{departmentOid}
and du.department_id = #{departmentId}
AND d.department_oid IN
#{departmentOid}
and cc.set_of_books_id = #{setOfBooksId}
AND p.PHONE_NUMBER = #{phoneNumber}
AND c.email = #{email}
and p.phone_number like concat(concat('%',#{phoneNumber}),'%')
and c.email like concat(concat('%',#{email}),'%')
and c.status!=1003
and c.employee_id like concat(concat('%',#{employeeId}),'%')
and c.full_name like concat(concat('%',#{fullName}),'%')
AND (c.full_name LIKE concat(concat('%',#{keyword}),'%') OR c.email LIKE concat(concat('%',#{keyword}),'%') OR c.employee_id LIKE concat(concat('%',#{keyword}),'%') OR p.phone_number LIKE concat(concat('%',#{keyword}),'%'))
AND ( c.full_name LIKE concat('%', concat(#{keyContact}, '%')) OR c.employee_id LIKE concat('%', concat(#{keyContact}, '%')) )
and ugu.user_group_id = #{userGroupId}
and ug.user_group_oid = #{userGroupOid}
order by c.employee_id,c.created_date
ORDER BY c.full_name

