SELECT t2.id contact_id, t2.user_id id, t2.user_oid, p.phone_number mobile, t2.leaving_date, t2.status, t2.employee_id, t2.full_name, t2.email, t2.title, t2.corporation_oid, t2.head_portrait avatar_oid, t2.gender, t2.gender_code, t2.direct_manager, t2.birthday, t2.duty, t2.duty_code, t2.rank_info, t2.rank_code, t2.employee_type, t2.employee_type_code, t2.entry_date, t4.name AS department_name, t4.path AS department_path, t4.manager_id, le.entity_name AS legal_entity_name FROM sys_contact t2 INNER JOIN sys_department_user t3 ON t2.user_id = t3.user_id INNER JOIN sys_department t4 ON t3.department_id = t4.id INNER JOIN sys_company t5 ON t2.company_id = t5.id LEFT JOIN sys_phone p ON p.contact_id=t2.id AND p.is_primary = 1 LEFT JOIN sys_legal_entity le ON le.legal_entity_oid = t2.corporation_oid WHERE t2.status != 1003
and t2.tenant_id = #{tenantId}
and t5.company_oid = #{companyOid}
and (t2.full_name like concat(concat('%',#{keyword}),'%') or t2.employee_id like concat(concat('%',#{keyword}),'%') or t2.email like concat(concat('%',#{keyword}),'%') or p.phone_number like concat(concat('%',#{keyword}),'%') )
and t2.status = #{status}
and t4.department_oid in
#{item}
and le.legal_entity_oid IN
#{item}
order by t2.status ,t2.employee_id

SELECT c.user_id id, c.user_oid AS userOid, c.full_name, c.title, c.employee_id AS employeeCode, c.company_id FROM sys_contact c WHERE c.employee_id=#{employeeCode}
AND c.company_id = #{companyId}
AND c.user_id IN (SELECT user_id from sys_department_user WHERE department_id = #{departmentId})

