SELECT d.department_code departmentCode, d.name NAME , d.id departmentId FROM sys_department d WHERE EXISTS( SELECT d.department_code departmentCode, d.name NAME , d.id departmentId FROM sys_department_group adg, sys_department_group_detail adgd WHERE adg.id = adgd.department_group_id AND adgd.dept_group_code = #{deptGroupCode} AND adgd.deleted = 0 AND d.status=101
AND adgd.department_id = d.id AND d.tenant_id=adg.tenant_id )
AND d.tenant_id=#{tenantId}

SELECT d.department_code departmentCode, d.name NAME , d.id departmentId, d.status FROM sys_department d WHERE d.tenant_id=#{tenantId}
AND d.status=#{status}
AND ( d.status = 101 OR d.status = 102 )
AND d.company_id = #{companyId}
AND d.department_code LIKE concat('%', concat(#{deptCode,jdbcType=VARCHAR}, '%'))
AND d.NAME LIKE concat('%', concat(#{deptDescription,jdbcType=VARCHAR}, '%'))
ORDER BY d.department_code

SELECT c.id id
FROM sys_company c WHERE c.enabled = 1
AND c.set_of_books_id = #{setOfBooks}
AND c.id NOT IN
#{companyId,jdbcType=BIGINT}
AND c.company_code LIKE concat('%', concat(#{companyCode,jdbcType=VARCHAR}, '%'))
AND c.name LIKE concat('%', concat(#{companyName,jdbcType=VARCHAR}, '%'))
AND c.company_code >= #{companyCodeFrom}
AND c.company_code <= #{companyCodeTo}
ORDER BY c.company_code

SELECT adg.id id, adg.tenant_id tenantId, adg.dept_group_code deptGroupCode, adg.description description, adg.company_id companyId, adg.enabled enabled, adg.deleted deleted, adg.created_date createdDate, adg.created_by createdBy, adg.last_updated_date lastUpdatedDate, adg.last_updated_by lastUpdatedBy FROM sys_department_group adg WHERE adg.tenant_id = #{tenantId}
AND adg.enabled = #{enable}
AND adg.dept_group_code LIKE concat('%', concat(#{deptGroupCode,jdbcType=VARCHAR}, '%'))
AND adg.description LIKE concat('%', concat(#{description,jdbcType=VARCHAR}, '%'))
ORDER BY adg.dept_group_code

SELECT c.user_id userId, c.user_oid userOid, c.full_name userName, c.employee_id userCode FROM sys_contact c WHERE c.employee_id=#{empId}
AND c.company_id = #{companyId}
AND u.id IN (SELECT user_id from sys_department_user WHERE department_id = #{departmentId})

SELECT c.user_oid FROM sys_contact c
WHERE c.tenant_id = #{tenantId}
AND ( c.full_name LIKE concat('%', concat(#{key,jdbcType=VARCHAR}, '%')) OR c.employee_id LIKE concat('%', concat(#{key,jdbcType=VARCHAR}, '%')) )

SELECT id FROM sys_user_group ug WHERE ug.company_oid IN( SELECT company_oid FROM sys_company c WHERE set_of_books_id = #{setOfBooksId}
AND c.enabled = #{enable}
)
AND ug.name LIKE concat('%', concat(#{name,jdbcType=VARCHAR}, '%'))
GROUP BY ug.id

SELECT id FROM sys_user_group ug WHERE ug.company_oid IN( SELECT company_oid FROM sys_company WHERE set_of_books_id = #{setOfBooksId}
AND c.enabled = #{enable}
) GROUP BY ug.id

SELECT c.user_oid FROM sys_contact c
WHERE c.tenant_id = #{tenantId}
AND ( c.full_name LIKE concat('%', concat(#{key,jdbcType=VARCHAR}, '%')) OR c.employee_id LIKE concat('%', concat(#{key,jdbcType=VARCHAR}, '%')) )
AND c.company_id = #{companyId}

SELECT c.id id
FROM sys_company c WHERE c.enabled = TRUE
AND c.set_of_books_id = #{setOfBooksId}
AND c.company_code LIKE concat('%', concat(#{companyCode,jdbcType=VARCHAR}, '%'))
AND c.name LIKE concat('%', concat(#{companyName,jdbcType=VARCHAR}, '%'))
AND c.company_code >= #{companyCodeFrom}
AND c.company_code <= #{companyCodeTo}
ORDER BY c.company_code

