mapper.update:
UPDATE art_user u
SET u.password_attempt = #{passwordAttempt},
u.lock_status = #{lockStatus},
u.lock_date_deadline = #{lockDateDeadline}
WHERE u.id = #{id}

UPDATE art_user u
    SET u.LANGUAGE = #{language}
    WHERE u.id = #{userId}

mapper.select:
SELECT AU.USER_OID, ac.full_name, ac.email, ac.employee_id, ac.title, au.login, au.id, au.is_deleted, au.status, au.language, au.lock_status, au.activated, au.tenant_id, aat.tenant_name, au.company_id, acc.name AS company_name, ap.phone_number AS mobile, acc.set_of_books_id, aso.set_of_books_name FROM art_user au, art_contact ac, art_phone ap, art_company acc, art_tenant aat, art_set_of_books aso WHERE ac.id = au.contact_id AND ap.contact_id = ac.id AND ap.is_primary = 1 AND acc.id=au.company_id AND aat.id = au.tenant_id AND aso.id = acc.set_of_books_id
and au.tenant_id = #{tenantId}
and acc.set_of_books_id = #{setOfBooksId}
and au.company_id = #{companyId}
and au.login like concat(concat('%',#{login}),'%')
and ac.full_name like concat(concat('%',#{fullName}),'%')
and ap.phone_number like concat(concat('%',#{mobile}),'%')
and ac.email like concat(concat('%',#{email}),'%')
ORDER BY ac.full_name

SELECT u.id, u.user_oid, u.login, c.full_name, d.name department_name, u.activated, t.phone_number mobile, u.status, u.leaving_date, c.duty, c.rank, c.employee_id, c.email, l.legal_entity_oid, cc.id company_id, cc.company_code, cc.tenant_id, cc.set_of_books_id, cc.name company_name, l.entity_name, d.department_oid, d.id department_id, c.gender, c.birthday, c.entry_date, f.role_oid finance_role_oid FROM art_user u LEFT JOIN art_contact c ON c.id = u.contact_id LEFT JOIN art_company cc ON cc.id = u.company_id INNER JOIN art_department_user du ON du.user_id = u.id LEFT JOIN art_department d ON d.id = du.department_id LEFT JOIN art_phone t ON (c.id=t.contact_id and t.is_primary=true) LEFT JOIN art_legal_entity l ON (l.legal_entity_oid = c.corporation_oid and l.tenant_id = u.tenant_id) LEFT JOIN atl_finance_role_user fru ON fru.user_id = u.id LEFT JOIN atl_finance_role f ON f.id = fru.role_id WHERE u.tenant_id = #{tenantId}
AND (c.full_name LIKE concat(concat('%',#{keyword}),'%') OR c.email LIKE concat(concat('%',#{keyword}),'%') OR c.employee_id LIKE concat(concat('%',#{keyword}),'%') OR t.phone_number LIKE concat(concat('%',#{keyword}),'%'))
AND d.department_oid IN
#{departmentOID}
AND cc.company_oid IN
#{companyOID}
AND u.status = #{status}
order by u.id
